                      '0%': { filter: 'drop-shadow(0 0 2px rgba(255,255,255,0.7))' },
                      '50%': { filter: 'drop-shadow(0 0 8px rgba(255,255,255,0.9))' },
                      '100%': { filter: 'drop-shadow(0 0 2px rgba(255,255,255,0.7))' }
